select count(distinct r.customer_id) as customerNames from customer c,category ca,film_category fc,inventory i,rental r where ca.category_id = fc.category_id and ca.name = "Action" and fc.film_id = i.film_id and i.inventory_id = r.inventory_id and c.customer_id = r.customer_id and c.active=1 order by c.first_name;